// Add a DELETE button (bootstrap btn danger) at the end of each row of the tracklist. 
// Attach the functionality to remove its row when it gets clicked. (Requires DOM manipulation)

function removeRow() {
  const btn = document.getElementById("btnDeleteRow");
  btn.onclick = function() {
    const row = document.getElementById("removeRow")
    row.forEach((row) => {
      row.classList.add("d-none");
    })
  }
}
removeRow()